func TestAdapter_NewAdapter(t *testing.T) {
	factory, err := adp.GetFactory("BadName")
	assert.Nil(t, factory)
	assert.NotNil(t, err)

	factory, err = adp.GetFactory(model.RegistryTypeAwsEcr)
	assert.Nil(t, err)
	assert.NotNil(t, factory)

	adapter, err := newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
		URL: "https://api.ecr.test-region.amazonaws.com",
	})
	assert.Nil(t, err)
	assert.NotNil(t, adapter)

	adapter, err = newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
		URL: "https://123456.dkr.ecr.test-region.amazonaws.com",
	})
	assert.Nil(t, err)
	assert.NotNil(t, adapter)

	adapter, err = newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
		URL: "https://api.ecr.test-china-region.amazonaws.com.cn",
	})
	assert.Nil(t, err)
	assert.NotNil(t, adapter)

	adapter, err = newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
		URL: "https://123456.dkr.ecr.test-china-region.amazonaws.com.cn",
	})
	assert.Nil(t, err)
	assert.NotNil(t, adapter)

	adapter, err = newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
	})
	assert.Nil(t, adapter)
	assert.NotNil(t, err)

	adapter, err = newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
		URL: "https://123456.dkr.ecr-fips.test-region.amazonaws.com",
	})
	assert.Nil(t, err)
	assert.NotNil(t, adapter)

	adapter, err = newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
		URL: "https://123456.dkr.ecr.us-isob-east-1.sc2s.sgov.gov",
	})
	assert.Nil(t, err)
	assert.NotNil(t, adapter)

	adapter, err = newAdapter(&model.Registry{
		Type: model.RegistryTypeAwsEcr,
		Credential: &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		},
		URL: "https://123456.dkr.ecr.us-iso-east-1.c2s.ic.gov",
	})
	assert.Nil(t, err)
	assert.NotNil(t, adapter)
}

func getMockAdapter(t *testing.T, hasCred, health bool) (*adapter, *httptest.Server) {
	server := test.NewServer(
		&test.RequestHandlerMapping{
			Method:  http.MethodGet,
			Pattern: "/v2/_catalog",
			Handler: func(w http.ResponseWriter, r *http.Request) {
				w.WriteHeader(http.StatusOK)
				w.Write([]byte(`
		{
			"repositories": [
					"test1"
			]
		}`))
			},
		},
		&test.RequestHandlerMapping{
			Method:  http.MethodGet,
			Pattern: "/v2/{repo}/tags/list",
			Handler: func(w http.ResponseWriter, r *http.Request) {
				w.WriteHeader(http.StatusOK)
				w.Write([]byte(`
			{
			    "name": "test1",
			    "tags": [
			        "latest"
			    ]
			}`))
			},
		},
		&test.RequestHandlerMapping{
			Method:  http.MethodGet,
			Pattern: "/v2/",
			Handler: func(w http.ResponseWriter, r *http.Request) {
				fmt.Println(r.Method, r.URL)
				if health {
					w.WriteHeader(http.StatusOK)
				} else {
					w.WriteHeader(http.StatusBadRequest)
				}
			},
		},
		&test.RequestHandlerMapping{
			Method:  http.MethodGet,
			Pattern: "/",
			Handler: func(w http.ResponseWriter, r *http.Request) {
				fmt.Println(r.Method, r.URL)
				w.WriteHeader(http.StatusOK)
			},
		},
		&test.RequestHandlerMapping{
			Method:  http.MethodPost,
			Pattern: "/",
			Handler: func(w http.ResponseWriter, r *http.Request) {
				fmt.Println(r.Method, r.URL)
				if buf, e := io.ReadAll(&io.LimitedReader{R: r.Body, N: 80}); e == nil {
					fmt.Println("\t", string(buf))
				}
				w.WriteHeader(http.StatusOK)
			},
		},
	)

	registry := &model.Registry{
		Type: model.RegistryTypeAwsEcr,
		URL:  server.URL,
	}

	var svc *awsecrapi.ECR
	if hasCred {
		registry.Credential = &model.Credential{
			AccessKey:    "xxx",
			AccessSecret: "ppp",
		}
		svc, _ = getAwsSvc(
			"test-region", registry.Credential.AccessKey, registry.Credential.AccessSecret, registry.Insecure, &server.URL)
	} else {
		svc, _ = getAwsSvc(
			"test-region", "", "", registry.Insecure, &server.URL)
	}
	return &adapter{
		registry: registry,
		Adapter:  native.NewAdapter(registry),
		cacheSvc: svc,
	}, server
}

func TestAdapter_Info(t *testing.T) {
	a, s := getMockAdapter(t, true, true)
	defer s.Close()
	info, err := a.Info()
	assert.Nil(t, err)
	assert.NotNil(t, info)

	assert.EqualValues(t, 1, len(info.SupportedResourceTypes))
	assert.EqualValues(t, model.ResourceTypeImage, info.SupportedResourceTypes[0])
}

func TestAdapter_HealthCheck(t *testing.T) {
	a, s := getMockAdapter(t, false, true)
	defer s.Close()
	status, err := a.HealthCheck()
	assert.Nil(t, err)
	assert.NotNil(t, status)
	assert.EqualValues(t, model.Healthy, status)

	a, s = getMockAdapter(t, true, false)
	defer s.Close()
	status, err = a.HealthCheck()
	assert.Nil(t, err)
	assert.NotNil(t, status)
	assert.EqualValues(t, model.Unhealthy, status)

	a, s = getMockAdapter(t, true, true)
	defer s.Close()
	status, err = a.HealthCheck()
	assert.Nil(t, err)
	assert.NotNil(t, status)
	assert.EqualValues(t, model.Healthy, status)
}

