func TestAdapter_FetchImages(t *testing.T) {
	a, s := getMockAdapter(t, true, true)
	defer s.Close()
	resources, err := a.FetchArtifacts([]*model.Filter{
		{
			Type:  model.FilterTypeName,
			Value: "*",
		},
		{
			Type:  model.FilterTypeTag,
			Value: "*",
		},
	})
	assert.Nil(t, err)
	assert.NotNil(t, resources)
	assert.Equal(t, 1, len(resources))
}

func TestAwsAuthCredential_Modify(t *testing.T) {
	et := time.Now().Add(time.Second).Unix()
	server := test.NewServer(
		&test.RequestHandlerMapping{
			Method:  http.MethodPost,
			Pattern: "/",
			Handler: func(w http.ResponseWriter, r *http.Request) {
				fmt.Println(r.Method, r.URL)
				if buf, e := io.ReadAll(&io.LimitedReader{R: r.Body, N: 80}); e == nil {
					fmt.Println("\t", string(buf))
				}
				w.WriteHeader(http.StatusOK)
				w.Write([]byte(fmt.Sprintf(`
{
   "authorizationData" : [
      {
         "expiresAt" : %d,
         "proxyEndpoint" : "https://12345.dkr.ecr.ap-northeast-1.amazonaws.com",
         "authorizationToken" : ""
      }
   ]
}
`, et)))
			},
		},
	)
	defer server.Close()
	svc, err := getAwsSvc(
		"test-region", "xxx", "ppp", true, &server.URL)
	require.Nil(t, err)
	a, _ := NewAuth("xxx", svc).(*awsAuthCredential)
	req := httptest.NewRequest(http.MethodGet, "https://1234.dkr.ecr.test-region.amazonaws.com/v2/", nil)
	err = a.Modify(req)
	require.Nil(t, err)
	err = a.Modify(req)
	require.Nil(t, err)
	time.Sleep(time.Second)
	err = a.Modify(req)
	require.Nil(t, err)
}

